INSERT INTO `soal` (`id_soal`,`id_ujian`,`jenis`,`soal`,`pilihan_1`,`pilihan_2`,`pilihan_3`,`pilihan_4`,`pilihan_5`,`parameter`,`kunci`,`urut`,`bobot`) VALUES ('31012','824','0','<p style=\"text-align: justify;\">Alasan mengapa para pendiri bangsa memilih pancasila sebagai dasar dan ideologi bangsa Indonesia karena (1.) Nilai yang terkandung dalam pancasila diambil dari kebudayaan dan adat istiadat bangsa Indonesia sebelum Indonesia membentuk sebuah negara (2.) Nilai yang terkandung didalam Pancasila sangat cocok untuk diterapkan di kehidupan sehari hari (3.) Ideologi Pancasila juga hanya mengatur manusia sebagai individu atau masyarakat dengan demikian Pancasila sah untuk dijadikan sebagai ideologi negara.</p>
